import * as React from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  NavigatingContainer,
  ImageBackground,
} from "react-native";

// TODO: CONFIGURE THE ICONS WHEN DEPLOYING: https://github.com/oblador/react-native-vector-icons
import { Button } from "react-native-elements";
import Icon from "react-native-vector-icons/FontAwesome";
import { Dimensions, PixelRatio } from "react-native";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import SelectList from "react-native-dropdown-select-list";

import AsyncStorage from "@react-native-async-storage/async-storage";
import { Header } from "react-native-elements";
import { SearchBar } from "react-native-elements";
import { Image } from "react-native";
import { Card, ListItem } from "react-native-elements";
import { getDownloadURL, getStorage } from "firebase/storage";
import { ref as refS } from "firebase/storage";
import * as ImagePicker from "expo-image-picker";
import { Touchable } from "react-native-web";
// import { createNativeStackNavigator } from "@react-navigation/native-stack";

// const Stack = createNativeStackNavigator();

export default function adScreen3({ navigation }) {
  const [url, setUrl] = React.useState();
  const [open, setOpen] = React.useState(false);
  const [value, setValue] = React.useState(null);
  const [items, setItems] = React.useState([
    { label: "Business Attire", value: "Business Attire" },
    { label: "Casual Wear", value: "banana" },
  ]);
  const [selected, setSelected] = React.useState("");
  const [numericPrice, numericChange] = React.useState("");
  const data1 = [
    { key: "1", value: "Rent" },
    { key: "2", value: "Sell" },

  ];

  const data2 = [
    { key: "1", value: "Used Fair" },

    { key: "2", value: "Normal Wear" },
    { key: "3", value: "Brand new" },
  ];
  const [titleLabel, onChangetitle] = React.useState(null);
  const [image, setImage] = React.useState(
    require("/Users/sanjayshreeyansgmail.com/Documents/Dev/react-cloth-sharing/ClothShare/upload-photo.jpeg")
  );
  var cou = 0;

  const didMount = React.useRef(false);

  //   React.useEffect(() => {
  //     const func = async () => {
  //       const storage = getStorage();
  //       const reference = refS(storage, "/image_sample.png");
  //       await getDownloadURL(reference).then((x) => {
  //         setUrl(x);
  //       });
  //     };
  //     func();
  //   }, []);

  //   console.log(url, cou++);

  const [search, updateSearch] = React.useState();
  console.log(search);
  const data = [{ key: "1", value: "Jammu & Kashmir" }];

  const getData = async () => {
    try {
      const value = await AsyncStorage.getItem("test");
      if (value !== null) {
        console.log("value from home: ", value);
      }
    } catch (e) {
      // error reading value
    }
  };
  getData();

  React.useEffect(() => {
    if (!didMount.current) {
      didMount.current = true;
      return;
    }

    if (url == undefined) {
      console.log(url, "!URL 1");
      document.title = "Threshold of over 1 reached.";
    } else {
      console.log(url, "!URL 3");
      global.users = [
        {
          name: "brynn",
          avatar: url,
        },
      ];

      document.title = "No threshold reached.";
    }
  }, [url]);

  function make_square_elms(value) {
    const screenWidth = Dimensions.get("window").width;
    const screenHeight = Dimensions.get("window").height;
    if (screenWidth > screenHeight) {
      console.log(screenWidth, screenHeight, "Choosing WUDTH");
      return wp(value);
    } else if (screenHeight > screenWidth) {
      console.log(screenWidth, screenHeight, "Choosing HEGHT");
      return hp(value);
    }
  }

  function changeUIBasedOnBuy () {

    console.log(selected)
    if (selected == 1) {

      console.log("The user choose to sell the product.")

    }

    else {

      console.log("The user choose to rent the product.")
    }


  }


function onChangedNumeric(txt) {

    let newText = '';
    let numbers = '0123456789';

    var text = txt.replace("$", )
    console.log(text)
    for (var i=0; i < text.length; i++) {
        if(numbers.indexOf(text[i]) > -1 ) {
            newText = newText + text[i];
        }
        else {
            // your call back function
            numericChange(newText);
            console.log("please enter numbers only");
        }
    }
    numericChange(newText);
}

  return (
    <View style={styles.container}>
      <View style={{ height: hp("5%") }}></View>
      {/* How to create a button i n react native */}
      <Text
        style={{
          fontWeight: "bold",
          color: "#c2c2c2",
          paddingBottom: hp("3%"),
        }}
      >
        {" "}
        Sell or Rent:{" "}
      </Text>
      <SelectList
        setSelected={setSelected}
        data={data1}
        boxStyles={{ width: wp("40%") }}
        onSelect={() => changeUIBasedOnBuy()}
        placeholder="Sell or Rent"
        // dropdownStyles={{ marginHorizontal: wp("20%"), width: wp("20") }}
        // onSelect={() => alert(selected)}
      />

      <Text
        style={{
          fontWeight: "bold",
          color: "#c2c2c2",
          paddingTop: hp("3%"),
          paddingBottom: hp("3%"),
        }}
      >
        Price:
      </Text>
      <TextInput
        style={styles.inputPrice}
        onEndEditing={numericChange}
        keyboardType="number-pad"
        onChangeText={(text) => onChangedNumeric(text)}
        value={"$" + numericPrice}
        placeholder="Tell us more about the product:"
        placeholderTextColor="black"
      />

      <View style={{ height: hp("5%") }}></View>

      <Button
        title="NEXT"
        raised={true}
        titleStyle={{ paddingLeft: 20 }}
        icon={
          <Icon
            name="arrow-right"
            size={(wp("4%"), hp("4%"))}
            color="white"
            iconStyle={{ paddingRight: 20 }}
          />
        }
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
