import {
  View,
  Text,
  SafeAreaView,
  FlatList,
  Image,
  StyleSheet,
} from "react-native";
import { Header } from "react-native-elements";
import { SearchBar } from "react-native-elements";
import * as React from "react";
export default function FlatListScreen({navigation}) {
  const [search, updateSearch] = React.useState();
  const animals = [
    {
      id: 1,
      name: "Shark",
      image: require("../assets/favicon.png"),
    },
    {
      id: 2,
      name: "Herring",
      image: require("../assets/favicon.png"),
    },
    {
      id: 3,
      name: "Lion",
      image: require("../assets/favicon.png"),
    },
    {
      id: 4,
      name: "Polar Bear",
      image: require("../assets/favicon.png"),
    },
    {
      id: 5,
      name: "Penguin",
      image: require("../assets/favicon.png"),
    },
    {
      id: 6,
      name: "Parrot",
      image: require("../assets/favicon.png"),
    },
    {
      id: 7,
      name: "Parrot",
      image: require("../assets/favicon.png"),
    },
    {
      id: 8,
      name: "Parrot",
      image: require("../assets/favicon.png"),
    },
    {
      id: 9,
      name: "Parrot",
      image: require("../assets/favicon.png"),
    },
  ];

  const oneAnimal = ({ item }) => (
    <View style={styles.item}>
      <View style={styles.avatarContainer}>
        <Image source={item.image} style={styles.avatar} />
      </View>
      <Text style={styles.name}>{item.name}</Text>
    </View>
  );

  const headerComponent = () => {
    return <Text style={styles.listHeadline}>Animals</Text>;
  };

  const listSeparator = () => {
    return <View style={styles.separator} />;
  };

  return (
    <View style={styles.container}>
      <Header
        elevated={true}
        // containerStyle={styles.container}
        leftComponent={<Text style={styles.paragraph}>ClothShare</Text>}
        centerComponent={
          <SearchBar
            round={true}
            clearIcon={true}
            lightTheme={true}
            placeholderTextColor={"black"}
            containerStyle={styles.containerSearch}
            inputContainerStyle={styles.containerSearch2}
            inputStyle={{ color: "black" }}
            placeholder="Type Here..."
            onChangeText={updateSearch}
            value={search}
          />
        }
        rightComponent={{ icon: "home", color: "#fff" }}
      />

      <FlatList
        ListHeaderComponentStyle={styles.listHeader}
        ListHeaderComponent={headerComponent}
        ListEmptyComponent={<Text>This List is a very Flat list</Text>}
        data={animals}
        renderItem={oneAnimal}
        ItemSeparatorComponent={listSeparator}
        // keyExtractor={ animals => animals.whatever }
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginHorizontal: 21,
  },

  listHeader: {
    height: 55,
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 8,
    borderBottomWidth: 1,
    borderBottomColor: "#7B52AB",
  },

  listHeadline: {
    color: "#333333",
    fontWeight: "bold",
    fontSize: 21,
  },

  item: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    paddingVertical: 13,
  },

  avatarContainer: {
    backgroundColor: "#D9D9D9",
    borderRadius: 100,
    height: 89,
    width: 89,
    justifyContent: "center",
    alignItems: "center",
  },

  avatar: {
    height: 55,
    width: 55,
  },

  name: {
    fontWeight: "600",
    fontSize: 16,
    marginLeft: 13,
  },

  separator: {
    height: 1,
    width: "100%",
    backgroundColor: "#CCC",
  },
});
