import {
  View,
  Text,
  SafeAreaView,
  FlatList,
  Image,
  TouchableOpacity,
  StyleSheet,
} from "react-native";
import { Header } from "react-native-elements";
import { SearchBar } from "react-native-elements";
import * as React from "react";
import { connectSearchBox } from "react-instantsearch-dom";

// how to import  connectSearchBox from algolia react instant search?


import { getDownloadURL, getStorage } from "firebase/storage";
import { ref as refS } from "firebase/storage";   

import { Icon } from "react-native-elements";
import algoliasearch from 'algoliasearch'
import {InstantSearch, SearchBox, Hits} from 'react-instantsearch-dom'

import { InfiniteHits } from "react-instantsearch-dom";

export default function FlatListScreen({navigation, route}) {
  const ALGOLIA_ID = "2RC54JEYQ4";
  const Search_ID = "213f763bef6a347d940a29db2e8e2f42";

  const searchClient = algoliasearch(ALGOLIA_ID, Search_ID)

   const [search , updateSearch] = React.useState();
  const [url, setUrl] = React.useState();

  React.useEffect(() => {
    const func = async () => {
      const storage = getStorage();
      const reference = refS(storage, "/image_sample.png");
      await getDownloadURL(reference).then((x) => {
        setUrl(x);
      });
    };
    func();
  }, []);



  //  React.useLayoutEffect(() => {

  // }, [navigation]);
  const animals = [
    {
      id: 1,
      name: "Shark",
      image: url,
    },
    {
      id: 2,
      name: "Herring",
      image: url,
    },
    {
      id: 3,
      name: "Lion",
      image: url,
    },
    {
      id: 4,
      name: "Polar Bear",
      image: url,
    },
    {
      id: 5,
      name: "Penguin",
      image: url,
    },
    {
      id: 6,
      name: "Parrot",
      image: url,
    },
    {
      id: 7,
      name: "Parrot",
      image: url,
    },
    {
      id: 8,
      name: "Parrot",
      image: url,
    },
    {
      id: 9,
      name: "Parrot",
      image: url,
    },
  ];

  const oneAnimal = ({ item }) => (
    <View style={styles.item}>
      <View style={styles.avatarContainer}>
        <Image source={{uri: item.image}} style={styles.avatar} />
      </View>
      <Text style={styles.name}>{item.name}</Text>
    </View>
  );

  const headerComponent = () => {
    return <Text style={styles.listHeadline}>Animals</Text>;
  };

  const listSeparator = () => {
    return <View style={styles.separator} />;
  };

  const SearchBox = () => {
    return (
      <SearchBar
        round={true}
        clearIcon={true}
        lightTheme={true}
        placeholderTextColor={"black"}
        containerStyle={styles.containerSearch}
        inputContainerStyle={styles.containerSearch2}
        inputStyle={{ color: "black" }}
        placeholder="Type Here..."
        onChangeText={updateSearch}
        value={search}
      />
    );
  };

  const Hit = ({ hit }) => {
    return (
      <View>
        <View style={styles.item}>
          <View style={styles.avatarContainer}>
            <Image source={{ uri: hit.image }} style={styles.avatar} />
          </View>
          <Text style={styles.name}>{hit.Title}</Text>
        </View>
        <View style={styles.separator} />;
      </View>
    );

  }
const CustomSearchBox = connectSearchBox(SearchBox);
const virtualSearchBox = connectSearchBox(() => {});
  return (
    <View style={styles.container}>
      <Header
        // containerStyle={styles.HeaderStyle}
        elevated={true}
        // containerStyle={styles.container}
        leftComponent={
          <View style={styles.HeaderStyle}>
            <Icon
              raised
              name="arrow-back"
              type="material"
              color="#f50"
              onPress={() => navigation.navigate("HomeScreen")}
            />

            <Text style={styles.paragraph}>ClothShare</Text>
          </View>
        }
        // centerComponent={
          /* <SearchBar
          //   round={true}
          //   clearIcon={true}
          //   lightTheme={true}
          //   placeholderTextColor={"black"}
          //   containerStyle={styles.containerSearch}
          //   inputContainerStyle={styles.containerSearch2}
          //   inputStyle={{ color: "black" }}
          //   placeholder="Type Here..."
          //   onChangeText={updateSearch}
          //   value={search}
          // /> */
        // }
        rightComponent={{ icon: "home", color: "#fff" }}
      />
      <View style={styles.containerInsta}>
        <InstantSearch indexName="SearchCloth" searchClient={searchClient}>
          {/* <SearchBox
            style={styles.containerInsta2}
            max-width={100}
            translations={{
              submitTitle: "Submit your search query.",
              resetTitle: "Clear your search query.",
              placeholder: "Start Searching Now...",
            }}
          ></SearchBox> */}

          <CustomSearchBox>


          </CustomSearchBox>

          <InfiniteHits hitComponent={Hit} style={styles.containerInsta} />
        </InstantSearch>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,

    // justifyContent: "center", //Centered vertically
    // alignItems: "center", // Centered horizontally
  },
  containerInsta: {
    flex: 1,
    alignItems: "left",
    justifyContent: "top",
    width: "100%",
    height: "100%",
    // justifyContent: "center", //Centered vertically
    // alignItems: "center", // Centered horizontally
  },

  containerInsta2: {
    flex: 1,
    alignItems: "center",
    justifyContent: "top",
    width: "100%",
    height: "100%",
    // justifyContent: "center", //Centered vertically
    // alignItems: "center", // Centered horizontally
  },

  listHeader: {
    height: 55,
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 8,
    borderBottomWidth: 1,
    borderBottomColor: "#7B52AB",
  },

  listHeadline: {
    color: "#333333",
    fontWeight: "bold",
    fontSize: 21,
  },

  item: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    paddingVertical: 13,
  },

  avatarContainer: {
    borderRadius: 100,
    height: 230,
    width: 89,
    justifyContent: "center",
    alignItems: "center",
    marginLeft: 100,
  },

  avatar: {
    height: 200,
    width: 200,
  },

  name: {
    fontWeight: "600",
    fontSize: 16,
    marginLeft: 100,
  },

  separator: {
    height: 1,
    width: "100%",
    backgroundColor: "#CCC",
  },
  containerSearch: {
    height: "35%",
    color: "white",
    width: "80%",
    paddingTop: 15,
    backgroundColor: "transparent",
    borderBottomColor: "transparent",
    borderTopColor: "transparent",
    borderTopWidth: 0, //works
    borderBottomWidth: 0, //works
  },
  containerSearch2: {
    textColor: "black",
    color: "black",
  },
  HeaderStyle: {
    width: "110%",
    height: "8%",
    flexDirection: "row",
  },
  paragraph: {
    margin: 24,
    fontSize: "1.5rem",
    fontWeight: "bold",
    textAlign: "center",
    color: "white",
  },
  paragraph2: {
    margin: 24,
    fontSize: "1.5rem",
    fontWeight: "bold",
    textAlign: "center",
    color: "black",
  },

  roundButton2: {
    width: 200,
    height: 50,
    paddingTop: 0,
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    padding: 0,
    borderRadius: 25,

    backgroundColor: "rgb(239,139,118)",
  },
});
