import * as React from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,

  TouchableOpacity,
  NavigatingContainer,
  ImageBackground,
} from "react-native";

// TODO: CONFIGURE THE ICONS WHEN DEPLOYING: https://github.com/oblador/react-native-vector-icons
import { Button } from "react-native-elements";
import Icon from "react-native-vector-icons/FontAwesome";
import { Dimensions, PixelRatio } from "react-native";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,

} from "react-native-responsive-screen";
import SelectList from "react-native-dropdown-select-list";

import AsyncStorage from "@react-native-async-storage/async-storage";
import { Header } from "react-native-elements";
import { SearchBar } from "react-native-elements";
import { Image } from "react-native";
import { Card, ListItem} from "react-native-elements";
import { getDownloadURL, getStorage } from "firebase/storage";
import { ref as refS } from "firebase/storage";
import * as ImagePicker from "expo-image-picker";
import { Touchable } from "react-native-web";
// import { createNativeStackNavigator } from "@react-navigation/native-stack";

// const Stack = createNativeStackNavigator();

export default function adScreen2({ navigation }) {
  const [url, setUrl] = React.useState();
  const [open, setOpen] = React.useState(false);
  const [value, setValue] = React.useState(null);
  const [items, setItems] = React.useState([
    { label: "Business Attire", value: "Business Attire" },
    { label: "Casual Wear", value: "banana" },
  ]); 
  const [selected, setSelected] = React.useState("");
  const [descriptionLabel, descriptionChange] = React.useState(null);
  const data1 = [

    { key: "1", value: "Business Attire" },
    { key: "2", value: "Casual Wear" },
    { key: "3", value: "Formal Wear" },
    { key: "4", value: "Sportswear" },
  ];

  const data2 = [
    { key: "1", value: "Used Fair" },

    { key: "2", value: "Normal Wear" },
    { key: "3", value: "Brand new" },
  ];
  const [titleLabel, onChangetitle] = React.useState(null);
  const [image, setImage] = React.useState(
    require("/Users/sanjayshreeyansgmail.com/Documents/Dev/react-cloth-sharing/ClothShare/upload-photo.jpeg")
  );
  var cou = 0;

  const didMount = React.useRef(false);

//   React.useEffect(() => {
//     const func = async () => {
//       const storage = getStorage();
//       const reference = refS(storage, "/image_sample.png");
//       await getDownloadURL(reference).then((x) => {
//         setUrl(x);
//       });
//     };
//     func();
//   }, []);

//   console.log(url, cou++);

  const [search, updateSearch] = React.useState();
  console.log(search);
   const data = [{ key: "1", value: "Jammu & Kashmir" }];

  const getData = async () => {
    try {
      const value = await AsyncStorage.getItem("test");
      if (value !== null) {
        console.log("value from home: ", value);
      }
    } catch (e) {
      // error reading value
    }
  };
  getData();


  React.useEffect(() => {
    if (!didMount.current) {
      didMount.current = true;
      return;
    }

    if (url == undefined) {
      console.log(url, "!URL 1");
      document.title = "Threshold of over 1 reached.";
    } else {
      console.log(url, "!URL 3");
      global.users = [
        {
          name: "brynn",
          avatar: url,
        },
      ];

      document.title = "No threshold reached.";
    }
  }, [url]);

  function make_square_elms (value){
    const screenWidth = Dimensions.get('window').width;
    const screenHeight = Dimensions.get('window').height;
    if (screenWidth > screenHeight) {
      console.log(screenWidth, screenHeight, "Choosing WUDTH")
      return wp(value)

    }

    else if (screenHeight > screenWidth) {
      console.log(screenWidth, screenHeight, "Choosing HEGHT");
      return hp(value);
    }

  }

  return (
    <View style={styles.container}>
      <View style={{ height: hp("5%") }}></View>
      {/* How to create a button i n react native */}
      <Text
        style={{
          fontWeight: "bold",
          color: "#c2c2c2",
          paddingBottom: hp("3%"),
        }}
      >
        {" "}
        Category:{" "}
      </Text>
      <SelectList
        setSelected={setSelected}
        data={data1}
        boxStyles={{ width: wp("40%") }}
        placeholder="Select a Category"
        // dropdownStyles={{ marginHorizontal: wp("20%"), width: wp("20") }}
        // onSelect={() => alert(selected)}
      />

      <Text
        style={{
          fontWeight: "bold",
          color: "#c2c2c2",
          paddingTop: hp("3%"),
          paddingBottom: hp("3%"),
        }}
      >
        Condition:
      </Text>
      <SelectList
        setSelected={setSelected}
        data={data2}
        boxStyles={{ width: wp("40%") }}
        placeholder="Select a Condition"
        // dropdownStyles={{ marginHorizontal: wp("20%"), width: wp("20") }}
        // onSelect={() => alert(selected)}
      />
      <Text
        style={{
          fontWeight: "bold",
          color: "#c2c2c2",
          paddingTop: hp("3%"),
          paddingBottom: hp("3%"),
        }}
      >
        Description:
      </Text>
      <TextInput
        style={styles.input}
        onEndEditing={descriptionChange}
        multiline={true}
        placeholder="Tell us more about the p:"
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    alignSelf: "center",
  },

  dropdown: {
    paddingVertical: hp("4%"),
    paddingHorizontal: wp("70%"),
    paddingTop: hp("2%"),
    borderRadius: wp("5%"),
    alignItems: "center",
    color: "#000000",

    justifyContent: "center",

  },

  containerCard: {
