import React from "react";
import { StyleSheet, View, Text, TouchableOpacity, SearchBox } from "react-native";
import PropTypes from "prop-types";
import { connectRefinementList } from "react-instantsearch-native";
import { InstantSearch } from "react-instantsearch-native";
import { InfiniteHits } from "react-instantsearch-dom";
const styles = StyleSheet.create({
  container: {
    padding: 10,
    backgroundColor: "#FFFFFF",
  },
  title: {
    alignItems: "center",
  },
  titleText: {
    fontSize: 20,
  },
  list: {
    marginTop: 20,
  },
  item: {
    paddingVertical: 10,
    flexDirection: "row",
    justifyContent: "space-between",
    borderBottom: 1,
    alignItems: "center",
  },
  itemCount: {
    backgroundColor: "#252b33",
    borderRadius: 25,
    paddingVertical: 5,
    paddingHorizontal: 7.5,
  },
  itemCountText: {
    color: "#FFFFFF",
    fontWeight: "800",
  },
});

export default function  RefinementList({ items, refine, navigation }) {
  const items2 = [
    {
      key: "1",
      name: "Shark",

    },
    {
      key: "2",
      name: "Herring",

    },
    {
      key: "3",
      name: "Lion",

    },
    {
      key: "4",
      name: "Polar Bear",

    },
    {
      key: "5",
      name: "Penguin",

    },
    {
      key: "6",
      name: "Parrot",

    },
    {
      key: "7",
      name: "Parrot",

    },
    {
      key: "8",
      name: "Parrot",

    },
    {
      key: "9",
      name: "Parrot",

    },
  ];
  return (
  <View style={styles.container}>
    <View style={styles.title}>
      <Text style={styles.titleText}>Brand</Text>
    </View>
    <View style={styles.list}>
      {items2.map((item) => {
        const labelStyle = {
          fontSize: 16,
          fontWeight: item.isRefined ? "800" : "400",
        };

        return (
          <TouchableOpacity
            key={items2.key}
            onPress={() => refine(item.name)}
            style={styles.item}
          >
            <Text style={labelStyle}>{item.label}</Text>
            <View style={styles.itemCount}>
              <Text style={styles.itemCountText}>{item.count}</Text>
            </View>

            <InstantSearch
              searchClient={items2}
              indexName="instant_search"
            >
              <SearchBox />
              <RefinementList attribute="brand" limit={5} />
              <InfiniteHits />
            </InstantSearch>
          </TouchableOpacity>
        );
      })}
    </View>
  </View>

  )
};
